mapper.select:
select 

from follow
where follow_id = #{followId,jdbcType=INTEGER}
  and user_name = #{userName,jdbcType=VARCHAR}

mapper.delete:
delete from follow
where follow_id = #{followId,jdbcType=INTEGER}
  and user_name = #{userName,jdbcType=VARCHAR}

mapper.insert:
insert into follow (follow_id, user_name, date_time
  )
values (#{followId,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{dateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update follow


    date_time = #{dateTime,jdbcType=TIMESTAMP},


where follow_id = #{followId,jdbcType=INTEGER}
  and user_name = #{userName,jdbcType=VARCHAR}

update follow
set date_time = #{dateTime,jdbcType=TIMESTAMP}
where follow_id = #{followId,jdbcType=INTEGER}
  and user_name = #{userName,jdbcType=VARCHAR}
